@staticmethod
def create_sequences(data, target, time_steps=1):
    xs, ys = [], []
    for i in range(len(data) - time_steps):
        x = data[i:(i + time_steps)]
        y = target[i + time_steps - 1]  # Align y with the last element in the sequence
        xs.append(x)
        ys.append(y)
    return np.array(xs), np.array(ys)
